var data = mdm.store.data__.api.supportAPI.connectionSummary;

tbl.forEach(function(tItem){
	tItem.Occurences = 0;
  	data.forEach(function(dItem){
    	if(dItem.ID==tItem.ID) tItem.Occurences = dItem.Occurences;
    });
  	results.push(tItem);
});

mdm.store.data__.variables.connStats = results;

